const mongoose = require('mongoose');
const Schema = mongoose.Schema;

const userSchema = new Schema({
    fullName: String,
    userName: {
        type:String,
        unique: true
    },
    email: String,
    password: String,
    confirmed: {
        type: Boolean,
        default:false
    },
    resetPasswordToken: String,
    emailConfirmToken: String,
    avatar: {
        type: String,
        default: "http://localhost:4000/user.png"
    },
    address: String,
    balance:{
        type:String,
        default:""
    },
    location: String,
    twoFactorEnabled: {
        type: Boolean,
        default:false
    },
    twoFactorSecret:{
        type:String,
        default:""
    },
    twoFactorCode: {
        type:String,
        default:""
    },
    type: {
        type: String,
        default: "USER",
    },
    kyc: {
        mobile:{
            type:String,
            default:""
        },
        birthDate: {
            type:String,
            default:""
        },// !
        nationality: {
            type:String,
            default:""
        },
        country: {
            type:String,
            default:""
        },
